import React, { useState } from "react";
import "./post.css"; // Importing the same CSS for consistent styling

const dummyPublicEvents = [
  {
    id: 1,
    like: 45,
    profilePicture: "https://static.vecteezy.com/system/resources/previews/019/896/008/original/male-user-avatar-icon-in-flat-design-style-person-signs-illustration-png.png",
    organizer: "Community Center",
    date: "1 day ago",
    desc: "Join our public community event for fun and engagement!",
    photo: "data:image/jpeg;base64,",
    comment: 23,
  },
  {
    id: 2,
    like: 60,
    profilePicture: "https://static.vecteezy.com/system/resources/previews/019/896/008/original/male-user-avatar-icon-in-flat-design-style-person-signs-illustration-png.png",
    organizer: "City Hall",
    date: "3 days ago",
    desc: "Public festival with live music and food stalls.",
    photo: "data:image/jpeg;base64,",
    comment: 15,
  },
];

export default function PublicEvents() {
  return (
    <div className="w-full flex flex-col items-center">
      <h1 className="text-2xl font-bold mb-6">Public Events</h1>
      <div className="w-full flex flex-col items-center gap-6">
        {dummyPublicEvents.map((event) => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
    </div>
  );
}

const EventCard = ({ event }) => {
  const [like, setLike] = useState(event.like);
  const [isLiked, setIsLiked] = useState(false);

  const likeHandler = () => {
    setLike(isLiked ? like - 1 : like + 1);
    setIsLiked(!isLiked);
  };

  const applyHandler = () => {
    alert(`You applied to the event organized by ${event.organizer}`);
  };

  return (
    <div className="post max-w-lg w-full bg-white shadow-md rounded-lg overflow-hidden">
      <div className="postWrapper p-4">
        {/* Top Section */}
        <div className="flex items-center gap-3">
          <img
            className="postProfileImg"
            src={event.profilePicture}
            alt="Organizer"
          />
          <div>
            <span className="font-medium text-lg">{event.organizer}</span>
            <span className="text-sm text-gray-500 block">{event.date}</span>
          </div>
        </div>

        {/* Center Section */}
        <div className="postCenter my-4">
          <span className="postText block mb-3">{event.desc}</span>
          <img
            className="postImg rounded-md w-full"
            src={event.photo}
            alt="Event"
          />
        </div>

        {/* Bottom Section */}
        <div className="postBottom flex justify-between items-center mt-4">
          <div className="postBottomLeft flex items-center">
            <img
              className="likeIcon cursor-pointer"
              src="https://cdn-icons-png.flaticon.com/512/10307/10307855.png"
              onClick={likeHandler}
              alt="Like"
            />
            <span className="postLikeCounter ml-2 text-sm text-gray-600">
              {like} people like it
            </span>
          </div>
          <div className="postBottomRight">
            <button
              className="applyButton bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
              onClick={applyHandler}
            >
              Apply
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
